package com.gs.pojo;import java.util.Date;//文章表public class Article implements java.io.Serializable {	private static final long serialVersionUID = 1L;	//field	/**编号  **/	private String id;	/**文章标题  **/	private String title;	/**文章摘要  **/	private String abstracts;	/**文章内容  **/	private String content;	/**文章图片路径  **/	private String logo;	/**创建时间  **/	private Date created_time;	private String article_type;	/**状态  **/	private String status;	//method	public String getArticle_type() {		return article_type;	}	public void setArticle_type(String article_type) {		this.article_type = article_type;	}	public String getId() {		return id;	}	public void setId(String id) {		this.id = id;	}	public String getTitle() {		return title;	}	public void setTitle(String title) {		this.title = title;	}	public String getAbstracts() {		return abstracts;	}	public void setAbstracts(String abstracts) {		this.abstracts = abstracts;	}	public String getContent() {		return content;	}	public void setContent(String content) {		this.content = content;	}	public String getLogo() {		return logo;	}	public void setLogo(String logo) {		this.logo = logo;	}	public String getStatus() {		return status;	}	public void setStatus(String status) {		this.status = status;	}	public Date getCreated_time() {		return created_time;	}	public void setCreated_time(Date created_time) {		this.created_time = created_time;	}}